mapper.select:
select * from ( select rownum rnum, a.* from (
select * from study_menu where menuid = #{menuid} order by board_num desc) a)
where rnum BETWEEN #{start} and #{end}

select * from ( select rownum rnum, a.* from (
select * from study_menu order by regdate desc) a)
where rnum BETWEEN #{start} and #{end}
